// src/metadata.js
var createEntity = (name, type, props = {}, children) => {
  const { label, datatype, ...meta } = props;
  const e = {
    name,
    type,
    addChild: function(e2, name2) {
      if (!this.children) {
        this.children = name2 ? {} : [];
      }
      if (name2) {
        if (Array.isArray(this.children)) {
          throw new Error("Cannot add a named entity to child array");
        }
        this.children[name2] = e2;
      } else {
        this.children.push(e2);
      }
    }
  };
  if (label) {
    e.label = label;
  }
  if (datatype) {
    e.datatype = datatype;
  }
  if (children) {
    e.children = children;
  }
  e.meta = meta || {};
  return e;
};
export {
  createEntity
};
